import { Box, Button, Text, Input } from '@chakra-ui/react';
import { useState } from 'react';
import useMutation from '../hooks/useMutation'; // Adjust to your correct path

const validFileTypes = ['image/jpeg', 'image/png', 'image/jpg'];
const URL = "/images";

const Posts = () => {
  // Set up custom mutation hook for image uploading
  const { mutate: uploadImage, isLoading: uploading, error: uploadError } = useMutation({ url: URL });
  const [error, setError] = useState('');

  const handleUpload = async (e) => {
    const file = e.target.files[0];
    console.log(file);

    if (!validFileTypes.includes(file.type)) {
      setError('Invalid file type. Please upload a valid image file.');
      return;
    }

    const form = new FormData();
    form.append('image', file);
    
    // Use uploadImage function from useMutation hook
    await uploadImage(form);
    setError(''); // Clear any existing error after upload
  };

  return (
    <Box mt={6}>
      <Input id='imageInput' type='file' hidden onChange={handleUpload} />
      <Button as='label' htmlFor='imageInput' colorScheme="blue" variant="outline" mb={4} cursor="pointer">
        {uploading ? "Uploading..." : "Upload"}
      </Button>

      {error && (
        <Text fontSize="lg" color="red.300">
          {error}
        </Text>
      )}

      {uploadError && (
        <Text fontSize="lg" color="red.300">
          {uploadError.message || "An error occurred during upload."}
        </Text>
      )}

      <Text textAlign="left" mb={4}>
        Posts
      </Text>
    </Box>
  );
};

export default Posts;
